mapper.insert:
insert into c_list(c_num,
uuid, title, content, regdate, c_code)
values(
(select nvl(max(c_num+1),
0) from c_list)
, #{uuid}
, #{title}
, #{content}
, sysdate
, #{c_code}
)

mapper.update:
		update c_list
		set
		title=#{title},
		content=#{content}
		where c_num=#{c_num}

mapper.select:
		select c_num, uuid, title, content, sysdate, c_code, checked, r
		from(
		select c_num, uuid, title, content, sysdate, c_code, checked, rownum r
		from(
		select c_num, uuid, title, content, sysdate, c_code, checked from
		c_list
		order by rownum asc
		)
		)

where r>=#{sno} and r<=#{eno}
