mapper.update:
update d_day set
	title = #{title},
	d_date = #{d_date}
where d_num = #{d_num}

mapper.select:
select d_num, title, d_date, regdate, r
from (
    select d_num, title, d_date, regdate, rownum r
    from(
        select d_num, title, d_date, regdate
        from d_day
        
			
				
					title like '%'||#{word}||'%'		
				
				
					and uuid=#{uuid}
				
			
		
        order by d_date asc
        )
)

where r>=#{sno} and r<=#{eno}

select count(*) from d_day
title like '%'||#{word}||'%'
and uuid=#{uuid}
