mapper.select:
		SELECT a_num, a_name, title, regdate, viewcnt, r
		FROM ( 
			  SELECT a_num, a_name, title, regdate, viewcnt, rownum r
			  FROM (
			  		SELECT a_num, a_name, title, regdate, viewcnt
			  		FROM a_board
                  	order by a_num
        	  )
		)
		WHERE r>=#{sno} and r<=#{eno}

mapper.update:
UPDATE a_board
SET	title = #{title},
	content = #{content}
WHERE a_num = #{a_num}


	UPDATE a_board
	SET viewcnt = viewcnt+1
	WHERE a_num= #{a_num}
