mapper.update:
update d_day set
	title = #{title},
	regdate = #{regdate}
where d_num = #{d_num}

mapper.select:
select d_num, title, regdate, r
from (
    select d_num, title, regdate, rownum r
    from(
        select d_num, title, regdate
        from d_day
        
			
				
					title like '%'||#{word}||'%'		
				
			
		
        order by d_num asc
        )
)

where r>=#{sno} and r<=#{eno}

select
count(*) from d_day
where d_num = #{d_num}
