mapper.select:
		SELECT a_num, a_name, title, regdate, r
		FROM ( 
			  SELECT a_num, a_name, title, regdate, rownum r
			  FROM (
			  		SELECT a_num, a_name, title, regdate
			  		FROM a_board
                  	order by a_num
        	  )
		)
		WHERE r>=#{sno} and r<=#{eno}

mapper.update:
UPDATE a_board
SET	title = #{title},
	content = #{content}
WHERE a_num = #{a_num}

mapper.select:
	SELECT count(*)
	FROM a_board
	WHERE a_num = #{a_num}
