mapper.select:
		SELECT r_num, r_name, content, regdate, r
		FROM ( 
			  SELECT r_num, r_name, content, regdate, rownum r
			  FROM (
			  		SELECT r_num, r_name, content, regdate
			  		FROM a_board_reply
                  	order by r_num
        	  )
		)
		WHERE r>=#{sno} and r<=#{eno}

mapper.update:
UPDATE a_board_reply
SET	content = #{content}
WHERE r_num = #{r_num}

mapper.select:
	SELECT count(*)
	FROM a_board_reply
	WHERE r_num = #{r_num}
